  CONSTRAINT `news_news_category_id_f060a768_fk_news_newscategory_id` FOREIGN KEY (`category_id`) REFERENCES `news_newscategory` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=20 DEFAULT CHARSET=utf8;

